mapper.select:
select A.cltcd as cltcd, A.custcd AS custcd, A.spjangcd as spjangcd, '' as deldate, '' as delnum, A.pcode as pcode, A.pname as pname, A.psize as psize, A.punit as punit, sum(A.qty) as qty, 0 as cuamt, 0 as uamt, sum(A.samt) as samt, sum(A.tamt) as tamt, sum(A.mamt) as mamt, '0' as taxflag, 'KRW' as moncls, 1 as monrate, sum(A.lotqty) as lotqty, '' as indate, '' as inperid FROM ( select A.delcltcd as cltcd, A.custcd AS custcd, A.spjangcd as spjangcd, '' as deldate, '' as delnum, A.pcode as pcode, B.phm_pnam as pname, B.phm_size as psize, B.phm_unit as punit, 0 as qty, 0 as cuamt, 0 as uamt, 0 as samt, 0 as tamt, 0 as mamt, '0' as taxflag, 'KRW' as moncls, 1 as monrate, 0 as lotqty, '' as indate, '' as inperid from TB_DA006_IMPORT_PANNEL A WITH (NOLOCK) Left OUTER JOIN TB_CA501 B ON pcode=B.phm_pcod where A.itemcd in
#{itemcd}
and A.color = #{color} and A.grade = #{grade} and A.thick = #{thick} and A.width = #{width} and Len(isnull(A.deldate,'')) = 0 union all select A. delcltcd as cltcd, A.custcd AS custcd, A.spjangcd as spjangcd, '' as deldate, '' as delnum, A.pcode as pcode, B.phm_pnam as pname, B.phm_size as psize, B.phm_unit as punit, 0 as qty, 0 as cuamt, 0 as uamt, 0 as samt, 0 as tamt, 0 as mamt, '0' as taxflag, 'KRW' as moncls, 1 as monrate, 0 as lotqty, '' as indate, '' as inperid from TB_DA006_IMPORT_WINGBADY A WITH (NOLOCK) Left OUTER JOIN TB_CA501 B ON pcode=B.phm_pcod where A.itemcd in
#{itemcd}
and A.color = #{color} and A.grade = #{grade} and A.thick = #{thick} and A.width = #{width} and Len(isnull(A.deldate,'')) = 0 ) A GROUP BY A.cltcd, A.custcd, A.spjangcd, A.pcode, A.pname, A.psize, A.punit

select A.delcltcd as cltcd,  A.custcd AS custcd, A.spjangcd as spjangcd, '' as deldate, '' as delnum, A.pcode as pcode, B.phm_pnam as pname, B.phm_size as psize,
        B.phm_unit as punit,  A.delcqty as qty, 0 as cuamt,  A.deluamt as uamt,  A.delsamt as samt, (A.delsamt * 0.1) as tamt,
         A.delsamt + (A.delsamt * 0.1) as mamt, '0' as taxflag, 'KRW' as moncls, 1 as monrate, A.coilqty as coilqty, '' as indate, '' as inperid
from TB_DA006_IMPORT_PANNEL A WITH (NOLOCK)
    Left OUTER JOIN TB_CA501 B ON pcode=B.phm_pcod
where A.itemcd = #{lotno}
  and A.color = #{color} and A.grade = #{grade} and A.thick = #{thick}
  and A.width = #{width} and Len(isnull(A.deldate,'')) = 0

union all


select  A.delcltcd as cltcd, A.custcd AS custcd, A.spjangcd as spjangcd, '' as deldate, '' as delnum, A.pcode as pcode, B.phm_pnam as pname, B.phm_size as psize,
         B.phm_unit as punit,  A.deljqty as qty, 0 as cuamt,  A.deluamt as uamt,  A.delsamt as samt, (A.delsamt * 0.1) as tamt,
         A.delsamt + (A.delsamt * 0.1) as mamt, '0' as taxflag, 'KRW' as moncls, 1 as monrate, A.coilqty as coilqty, '' as indate, '' as inperid
from TB_DA006_IMPORT_WINGBADY A WITH (NOLOCK)
    Left OUTER JOIN TB_CA501 B ON pcode=B.phm_pcod
where A.itemcd = #{lotno} and A.color = #{color} and A.grade = #{grade} and A.thick = #{thick}
  and A.width = #{width} and Len(isnull(A.deldate,'')) = 0

mapper.delete:
delete from tb_da036
where deldate = #{deldate} and delnum = #{delnum}
  and deldate + delnum not in (select deldate + delnum from tb_da037 where deldate = #{deldate} and delnum=#{delnum} )

mapper.update:
update tb_da035 set deldate = null, delnum = null, delseq =null
where deldate = #{deldate} and delnum=#{delnum}
 and deldate + delnum not in (select deldate + delnum from tb_da037 where deldate = #{deldate} and delnum=#{delnum} )

update TB_DA035
set spjangcd = 'ZZ'
,
deldate=#{deldate}
,
delnum=#{delnum}
,
delseq=#{delseq}
where  fdeldate = #{fdeldate} and fdelnum=#{fdelnum} and fdelseq=#{fdelseq}
